<?phpdefine('DTW', true);require_once dirname(__FILE__).'/include/init.php';if(!$m){    $m = 'login';}else{    $m = str_replace('/', '', $m);    $m = str_replace("\\", '', $m);}if(file_exists('./' . $m . '.admin.php')){    if ($m != 'login' && (!$_SESSION['nick'])){        echo '<script>alert("请登录！");top.location.href="index.php?m=login";</script>';        exit();    }	include './' . $m . '.admin.php';}elseif(file_exists('./' . $m . '.php')){    include './' . $m . '.php';}else{	header('HTTP/1.1 404 Not Found', true, 404);}